mapper.select:
select c.uid,c.name,ds.`group`,ds.zone,ds.seat_num,ds.written_score as score
from score_info ds left outer join contestant c on c.uid=ds.uid
where ds.`group`=#{group} and ds.zone=#{zone};

select si.uid,ui.name,si.practical_score as score from score_info si ,user_info ui

    si.uid=ui.uid
    and si.practical_score is not null

        and si,contestant_name like concat('%',contestantName,'%')

    and ui.`group`=#{group}
    and ui.zone=#{zone}

select si.uid,ui.name,si.q_and_a_score as score from score_info si ,user_info ui

    si.uid=ui.uid
    and si.practical_score !=null

        and si,contestant_name like concat('%',contestantName,'%')

    and ui.`group`=#{group}
    and ui.zone=#{zone}
