mapper.update:
update user
username=#{username}, password=#{password}, nickname=#{nickname}, rid=#{rid}, avatar=#{avatar}, enabled=#{enabled}, username_modifiable=#{usernameModifiable}
where id=#{id};

mapper.select:
select i.name,u.rid as role,i.`group`,i.zone,u.status,u.online_time from user u, user_info i

    u.id=i.uid
    and i.name like concat('%',#{name},'%')
    and u.rid=#{rid}
    and i.`group`=#{group}
    and i.zone=#{zone}

order by u.rid asc,i.group asc,i.zone asc,u.status desc,u.online_time asc
